import React, { useState, useEffect } from 'react';
import { Box, Container, TextField, Button, Typography, IconButton } from '@mui/material';
import { Wheel } from '@uiw/react-color';
import Jimp from 'jimp';
import Banner from './components/Banner';
import ThemeToggleIcon from './components/ThemeToggleIcon';
import themeManager from './theme.js';
import './theme.css';

const DEFAULT_IMAGE_URL = 'https://cdn.shopify.com/s/files/1/0804/1136/1573/files/HEXTRA-Master-1800.png?v=1736817806';
const DEFAULT_COLOR = '#dd0000';
const VERSION = '0.1.0'; // Get this from package.json

function App() {
  const [selectedColor, setSelectedColor] = useState(DEFAULT_COLOR);
  const [imageFile, setImageFile] = useState(null);
  const [imageUrl, setImageUrl] = useState(DEFAULT_IMAGE_URL);
  const [processedImage, setProcessedImage] = useState(null);
  const [error, setError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(document.documentElement.getAttribute('data-theme') === 'dark');

  const handleColorChange = (color) => {
    const hex = color.hex;
    if (/^#[0-9A-F]{6}$/i.test(hex)) {
      setSelectedColor(hex);
      setError('');
    } else {
      setError('Invalid HEX color code');
    }
  };

  const handleHexInput = (event) => {
    const hex = event.target.value;
    if (hex.startsWith('#') && hex.length <= 7) {
      setSelectedColor(hex);
      if (hex.length === 7 && !/^#[0-9A-F]{6}$/i.test(hex)) {
        setError('Invalid HEX color code');
      } else {
        setError('');
      }
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImageFile(file);
      setImageUrl('');
      setError('');
    }
  };

  const handleUrlChange = (event) => {
    setImageUrl(event.target.value);
    setImageFile(null);
  };

  const applyColor = async () => {
    try {
      setIsProcessing(true);
      setError('');

      let image;
      if (imageFile) {
        const buffer = await imageFile.arrayBuffer();
        image = await Jimp.read(Buffer.from(buffer));
      } else if (imageUrl) {
        image = await Jimp.read(imageUrl);
      } else {
        throw new Error('Please upload an image or provide an image URL');
      }

      const processImage = async (image) => {
        // Ensure image has alpha channel
        if (!image.hasAlpha()) {
          image.rgba(true);
        }

        image.scan(0, 0, image.bitmap.width, image.bitmap.height, function(x, y, idx) {
          // Get the current pixel's RGBA values
          const red = this.bitmap.data[idx + 0];
          const green = this.bitmap.data[idx + 1];
          const blue = this.bitmap.data[idx + 2];
          const alpha = this.bitmap.data[idx + 3];

          // Calculate luminance (brightness)
          const luminance = (red * 0.299 + green * 0.587 + blue * 0.114) / 255;

          // Only modify pixels that aren't fully transparent
          if (alpha > 0) {
            // Convert hex color to RGB
            const r = parseInt(selectedColor.slice(1, 3), 16);
            const g = parseInt(selectedColor.slice(3, 5), 16);
            const b = parseInt(selectedColor.slice(5, 7), 16);

            // Apply color while preserving luminance
            this.bitmap.data[idx + 0] = Math.round(r * luminance);
            this.bitmap.data[idx + 1] = Math.round(g * luminance);
            this.bitmap.data[idx + 2] = Math.round(b * luminance);
            // Preserve original alpha
            this.bitmap.data[idx + 3] = alpha;
          }
        });

        // Convert to base64
        const base64 = await image.getBase64Async(Jimp.MIME_PNG);
        setProcessedImage(base64);
        setError('');
      };

      await processImage(image);
    } catch (err) {
      console.error('Error processing image:', err);
      setError(err.message || 'Error processing image');
    } finally {
      setIsProcessing(false);
    }
  };

  useEffect(() => {
    themeManager.init();
    setIsDarkMode(document.documentElement.getAttribute('data-theme') === 'dark');
    // Process the default image when the component mounts
    if (imageUrl) {
      applyColor();
    }
  }, []);

  const handleThemeToggle = () => {
    themeManager.toggle();
    setIsDarkMode(document.documentElement.getAttribute('data-theme') === 'dark');
  };

  return (
    <>
      <Banner 
        version={VERSION}
        isDarkMode={isDarkMode}
        onThemeToggle={handleThemeToggle}
      />
      <Container maxWidth="md">
        <Box
          sx={{
            backgroundColor: (theme) => theme.palette.background.paper,
            borderRadius: '8px',
            boxShadow: (theme) => theme.shadows[1],
            p: 4,
            minHeight: 'calc(100vh - 140px)' // Account for banner and padding
          }}
        >
          <Typography variant="h4" component="h1" sx={{ mb: 4 }}>
            HEXTRA Color Application
          </Typography>

          {/* Color selection section */}
          <Box sx={{ 
            display: 'flex', 
            gap: 4, 
            alignItems: 'flex-start',
            mb: 4
          }}>
            <Box sx={{ 
              width: 240,
              height: 240,
              p: 2
            }}>
              <Wheel
                color={selectedColor}
                onChange={(color) => handleColorChange(color)}
                style={{ width: '100%', height: '100%' }}
              />
            </Box>

            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, flex: 1 }}>
              <TextField
                label="HEX Color"
                value={selectedColor}
                onChange={handleHexInput}
                error={!!error}
                helperText={error}
                fullWidth
              />
              <Box
                sx={{
                  width: 80,
                  height: 80,
                  backgroundColor: selectedColor,
                  border: (theme) => `1px solid ${theme.palette.divider}`,
                  borderRadius: '4px'
                }}
              />
            </Box>
          </Box>

          {/* Image upload section */}
          <Box sx={{ 
            display: 'flex', 
            flexDirection: 'column', 
            gap: 2,
            mb: 4
          }}>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              style={{ marginBottom: '16px' }}
            />
            <Typography variant="body1" sx={{ my: 1 }}>OR</Typography>
            <TextField
              label="Image URL"
              value={imageUrl}
              onChange={handleUrlChange}
              fullWidth
              sx={{ mb: 2 }}
            />
            <Button
              variant="contained"
              onClick={applyColor}
              disabled={isProcessing || !selectedColor || (!imageFile && !imageUrl)}
              sx={{ 
                alignSelf: 'flex-end',
                minWidth: '120px'
              }}
            >
              {isProcessing ? 'Processing...' : 'Apply Color'}
            </Button>
          </Box>

          {error && (
            <Typography color="error" sx={{ mt: 2 }}>
              {error}
            </Typography>
          )}

          {/* Result section */}
          {processedImage && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h6" sx={{ mb: 2 }}>Processed Image:</Typography>
              <img
                src={processedImage}
                alt="Processed"
                style={{ 
                  maxWidth: '100%',
                  display: 'block',
                  borderRadius: '4px'
                }}
              />
            </Box>
          )}
        </Box>
      </Container>
    </>
  );
}

export default App;
